get(1005) -> #talk{talkid = 1005,content = ?T("<w><font size='16' color='#fff9d1'>     那头浑沌异种</font></w><w><font size='16' color='#12ff00'>烛九阴</font></w><w><font size='16' color='#fff9d1'>被妖气所迷失去灵智，上仙得灵剑相助，可一试剑锋！    </font></w>")};
get(1006) -> #talk{talkid = 1006,content = ?T("<w><font size='16' color='#fff9d1'>     你身怀神血，难怪得到神器认主，这</font></w><w><font size='16' color='#12ff00'>不归砚</font></w><w><font size='16' color='#fff9d1'>能控制时空，威力无穷，我来教你控制的法门。    </font></w>")};
get(1007) -> #talk{talkid = 1007,content = ?T("<w><font size='16' color='#fff9d1'>     镇压神器之灵，前去寻找十三    </font></w>")};
get(1008) -> #talk{talkid = 1008,content = ?T("<w><font size='16' color='#fff9d1'>     这里到处都是妖魔，宝儿想必吓坏了吧？我留在这等她，上仙你先去向</font></w><w><font size='16' color='#12ff00'>蜀山掌门</font></w><w><font size='16' color='#fff9d1'>报告吧！    </font></w>")};
get(1009) -> #talk{talkid = 1009,content = ?T("<w><font size='16' color='#fff9d1'>     妖神擅长蛊惑人心，只怕各派也遭他祸害。请上仙携我信物，同宝儿十三</font></w><w><font size='16' color='#12ff00'>下山</font></w><w><font size='16' color='#fff9d1'>传告各派，顺带扫清路上妖魔。    </font></w>")};
get(1010) -> #talk{talkid = 1010,content = ?T("<w><font size='16' color='#fff9d1'>     镇压白虎之灵    </font></w>")};
get(1011) -> #talk{talkid = 1011,content = ?T("<w><font size='16' color='#fff9d1'>     上仙，我等你很久啦！顺带在路上还抓了只可爱的</font></w><w><font size='16' color='#12ff00'>灵宠</font></w><w><font size='16' color='#fff9d1'>，呐，送给你吧~    </font></w>")};
get(1012) -> #talk{talkid = 1012,content = ?T("<w><font size='16' color='#fff9d1'>     清扫水族妖魔    </font></w>")};
get(1013) -> #talk{talkid = 1013,content = ?T("<w><font size='16' color='#fff9d1'>     清扫水族妖魔    </font></w>")};
get(1014) -> #talk{talkid = 1014,content = ?T("<w><font size='16' color='#fff9d1'>     上仙你来的正好，</font></w><w><font size='16' color='#12ff00'>长留派</font></w><w><font size='16' color='#fff9d1'>方才传书向我蜀山求援，定是发生了什么变故。我们快</font></w><w><font size='16' color='#12ff00'>前往长留</font></w><w><font size='16' color='#fff9d1'>，去看看情况吧！    </font></w>")};
get(1015) -> #talk{talkid = 1015,content = ?T("<w><font size='16' color='#fff9d1'>     小骨师姐偷走了神器</font></w><w><font size='16' color='#12ff00'>玄镇尺</font></w><w><font size='16' color='#fff9d1'>，浅薰师姐成了叛徒，还来了一帮坏蛋打伤掌门，蜀山来的上仙，快帮帮我们吧！    </font></w>")};
get(1016) -> #talk{talkid = 1016,content = ?T("<w><font size='16' color='#fff9d1'>     清除宵小，干掉刀手    </font></w>")};
get(1017) -> #talk{talkid = 1017,content = ?T("<w><font size='16' color='#fff9d1'>     镇压玄镇尺器灵    </font></w>")};
get(1018) -> #talk{talkid = 1018,content = ?T("<w><font size='16' color='#fff9d1'>     我长留家门不幸，逆徒不仅盗走神器，还污染了镇塔神兵，还请上仙友将这消息告知</font></w><w><font size='16' color='#12ff00'>绝情殿长老</font></w><w><font size='16' color='#fff9d1'>。    </font></w>")};
get(1019) -> #talk{talkid = 1019,content = ?T("<w><font size='16' color='#fff9d1'>     神兵被叛徒以瘴气所污，只有借助龙脉之力才能恢复力量。此事我无能为力，请上仙询问</font></w><w><font size='16' color='#12ff00'>我派掌门</font></w><w><font size='16' color='#fff9d1'>，以求对策。    </font></w>")};
get(1020) -> #talk{talkid = 1020,content = ?T("<w><font size='16' color='#fff9d1'>     叛徒我已亲手处置，龙脉位于帝都皇城，但那帝王与我有些恩怨，你先提高些修为，可代我往</font></w><w><font size='16' color='#12ff00'>长安</font></w><w><font size='16' color='#fff9d1'>一行。    </font></w>")};
get(1021) -> #talk{talkid = 1021,content = ?T("<w><font size='16' color='#fff9d1'>     掌门亲手将叛徒小骨钉上诛仙柱，一定是她怨气不息，在这</font></w><w><font size='16' color='#12ff00'>诛仙柱上</font></w><w><font size='16' color='#fff9d1'>闹鬼，我最怕鬼了~上仙，你代我去除了她吧~    </font></w>")};
get(1022) -> #talk{talkid = 1022,content = ?T("<w><font size='16' color='#fff9d1'>     我是小骨，是叛徒残留的一抹依恋，有件事要拜托你：师父重伤未愈，除非寻得</font></w><w><font size='16' color='#12ff00'>神医</font></w><w><font size='16' color='#fff9d1'>，此去长安，请你多留意。    </font></w>")};
get(1023) -> #talk{talkid = 1023,content = ?T("<w><font size='16' color='#fff9d1'>     小骨既爱着师父，为何又会背叛？这里面一定有什么古怪！只是要寻</font></w><w><font size='16' color='#12ff00'>神医</font></w><w><font size='16' color='#fff9d1'>，无处着手，当向何处去打听呢？    </font></w>")};
get(1024) -> #talk{talkid = 1024,content = ?T("<w><font size='16' color='#fff9d1'>     只要多吃</font></w><w><font size='16' color='#12ff00'>坐骑进阶丹</font></w><w><font size='16' color='#fff9d1'>，宝马就能化形。看这</font></w><w><font size='16' color='#12ff00'>幽冥狼</font></w><w><font size='16' color='#fff9d1'>，帅不帅气？听说长安有人摆下擂台，提升了修为我们也闯一闯去~    </font></w>")};
get(1025) -> #talk{talkid = 1025,content = ?T("<w><font size='16' color='#fff9d1'>     这位上仙好身手，将来定能在</font></w><w><font size='16' color='#12ff00'>竞技场</font></w><w><font size='16' color='#fff9d1'>中拔得头筹！</font></w><w><font size='16' color='#12ff00'>蜀山值守弟子</font></w><w><font size='16' color='#fff9d1'>传来讯息，请这位贵客前去一叙，这边请！    </font></w>")};
get(1026) -> #talk{talkid = 1026,content = ?T("<w><font size='16' color='#fff9d1'>     上仙是我蜀山的大恩人！我等自从知道上仙需以龙脉之力重启神兵，早为上仙联络了</font></w><w><font size='16' color='#12ff00'>护国使者</font></w><w><font size='16' color='#fff9d1'>，上仙只需前去就是！    </font></w>")};
get(1027) -> #talk{talkid = 1027,content = ?T("<w><font size='16' color='#fff9d1'>     搞定~嘻嘻，这神兵跟你呆久了，似乎看上你了哩！听蜀山的人说，你要找名医？去找</font></w><w><font size='16' color='#12ff00'>皇城守军</font></w><w><font size='16' color='#fff9d1'>问问吧，他可清楚哩！    </font></w>")};
get(1028) -> #talk{talkid = 1028,content = ?T("<w><font size='16' color='#fff9d1'>     要找神医？俺平生只服气强者真汉子！后生，只要你挑战过秘境</font></w><w><font size='16' color='#12ff00'>进阶副本</font></w><w><font size='16' color='#fff9d1'>，俺就即刻放你过去！    </font></w>")};
get(1029) -> #talk{talkid = 1029,content = ?T("<w><font size='16' color='#fff9d1'>     看样子大个子没指错路，那个</font></w><w><font size='16' color='#12ff00'>赛华佗</font></w><w><font size='16' color='#fff9d1'>一定就是我们要找的人！快快，喂完马，我们赶紧过去！    </font></w>")};
get(1030) -> #talk{talkid = 1030,content = ?T("<w><font size='16' color='#fff9d1'>     你先将这灵丹予他服下，要彻底治好，得寻到稀世灵药才行。只是按这伤势，白长留早该死了，难道有人为救他肯逆天改命？怪了……    </font></w>")};
get(1031) -> #talk{talkid = 1031,content = ?T("<w><font size='16' color='#fff9d1'>     上仙，不好啦，</font></w><w><font size='16' color='#12ff00'>妖神殿</font></w><w><font size='16' color='#fff9d1'>的人打上门来了，伤了好多同门，呜呜，怎么办啊！    </font></w>")};
get(1032) -> #talk{talkid = 1032,content = ?T("<w><font size='16' color='#fff9d1'>     妖神殿欺人太甚！他们逼我们交出神器，神器早就被小骨盗走了，哪里还有什么神器！我身单力薄，上仙，求你前去</font></w><w><font size='16' color='#12ff00'>支援师妹</font></w><w><font size='16' color='#fff9d1'>！    </font></w>")};
get(1033) -> #talk{talkid = 1033,content = ?T("<w><font size='16' color='#fff9d1'>     小骨盗走神器，就是为了解开</font></w><w><font size='16' color='#12ff00'>妖神的封印</font></w><w><font size='16' color='#fff9d1'>！他们故意打伤小师妹，是想逼受伤的掌门出手，上仙，帮帮我们！    </font></w>")};
get(1034) -> #talk{talkid = 1034,content = ?T("<w><font size='16' color='#fff9d1'>     引洪荒之力入体，激怒之下变身战神    </font></w>")};
get(1035) -> #talk{talkid = 1035,content = ?T("<w><font size='16' color='#fff9d1'>     上仙你好威风啊，一下就干掉了他们全部！拜托你除掉旁边的</font></w><w><font size='16' color='#12ff00'>妖鸟</font></w><w><font size='16' color='#fff9d1'>，我就可以回师姐那里去了！    </font></w>")};
get(1036) -> #talk{talkid = 1036,content = ?T("<w><font size='16' color='#fff9d1'>     妖神殿位于</font></w><w><font size='16' color='#12ff00'>蛮荒</font></w><w><font size='16' color='#fff9d1'>，本是妖魔信仰结成的组织，最近却大举进攻玄门各派抢夺神器，请上仙为我们主持公道！    </font></w>")};
get(1037) -> #talk{talkid = 1037,content = ?T("<w><font size='16' color='#fff9d1'>     听说那个背叛的夏浅薰也加入了</font></w><w><font size='16' color='#12ff00'>妖神殿</font></w><w><font size='16' color='#fff9d1'>，竟然对自己的同门下手，罪不可赦！走，带上十三，这就去找他们算账去！    </font></w>")};
get(1038) -> #talk{talkid = 1038,content = ?T("<w><font size='16' color='#fff9d1'>     那些木灵真是的，不就是不小心踩了他们几脚吗！还好他们不会爬树……我打听到</font></w><w><font size='16' color='#12ff00'>长安</font></w><w><font size='16' color='#fff9d1'>有人知道妖神殿的消息，我们这就出发吧！    </font></w>")};
get(1039) -> #talk{talkid = 1039,content = ?T("<w><font size='16' color='#fff9d1'>     清扫此处妖魔    </font></w>")};
get(1040) -> #talk{talkid = 1040,content = ?T("<w><font size='16' color='#fff9d1'>     名为妖神殿，难道里面都是</font></w><w><font size='16' color='#12ff00'>妖神的信徒</font></w><w><font size='16' color='#fff9d1'>？这些妖魔怎么不长眼，信个只会搞破坏的神干什么呀！我们先打探下情报吧！    </font></w>")};
get(1041) -> #talk{talkid = 1041,content = ?T("<w><font size='16' color='#fff9d1'>     唉，去蛮荒？那里很危险哒，我绩效还没完成呢！上仙，我看你红鸾星马上就要动，不如去</font></w><w><font size='16' color='#12ff00'>鸾凤仙台</font></w><w><font size='16' color='#fff9d1'>上寻个如意仙侣再一起出发？    </font></w>")};
get(1042) -> #talk{talkid = 1042,content = ?T("<w><font size='16' color='#fff9d1'>     这两个冤家又吵架了，宝儿虽然丢了</font></w><w><font size='16' color='#12ff00'>香囊</font></w><w><font size='16' color='#fff9d1'>，心里一定后悔，还是麻烦上仙找来还给她吧。    </font></w>")};
get(1043) -> #talk{talkid = 1043,content = ?T("<w><font size='16' color='#fff9d1'>     人家好不容易鼓起勇气，准备好</font></w><w><font size='16' color='#12ff00'>金粉佳人玫瑰</font></w><w><font size='16' color='#fff9d1'>要送给他的，这呆头鹅真要气死我，送你算啦！    </font></w>")};
get(1044) -> #talk{talkid = 1044,content = ?T("<w><font size='16' color='#fff9d1'>     上仙来得正好，原来妖神殿与人界曾有贸易，长安有</font></w><w><font size='16' color='#12ff00'>传送阵</font></w><w><font size='16' color='#fff9d1'>可直接抵达，我们这就出发吧！    </font></w>")};
get(1045) -> #talk{talkid = 1045,content = ?T("<w><font size='16' color='#fff9d1'>     妖魔崇尚武勇，其中却也有异数。那</font></w><w><font size='16' color='#12ff00'>妖魔掮客</font></w><w><font size='16' color='#fff9d1'>常在人间往来，认钱不认人，你倒可去寻他相助一二。    </font></w>")};
get(1046) -> #talk{talkid = 1046,content = ?T("<w><font size='16' color='#fff9d1'>     妖神殿闹叛乱，正打得厉害呢，圣君都躲出来了！你若执意要去，去问</font></w><w><font size='16' color='#12ff00'>蛊婆婆</font></w><w><font size='16' color='#fff9d1'>，但先得替我办了事才行。    </font></w>")};
get(1047) -> #talk{talkid = 1047,content = ?T("<w><font size='16' color='#fff9d1'>     九天雷电所铸法器，威力强大无比。     </font></w>")};
get(1048) -> #talk{talkid = 1048,content = ?T("<w><font size='16' color='#fff9d1'>     桀桀，死要钱的介绍你来，那就该知道规矩。要找妖神殿，先替老身办几桩小事，再去寻那前方</font></w><w><font size='16' color='#12ff00'>老者</font></w><w><font size='16' color='#fff9d1'>便是！    </font></w>")};
get(1049) -> #talk{talkid = 1049,content = ?T("<w><font size='16' color='#fff9d1'>     虎有伤人意，人有灭虎心。     </font></w>")};
get(1050) -> #talk{talkid = 1050,content = ?T("<w><font size='16' color='#fff9d1'>     牛魔王都不敢拦我的路，何况是你。     </font></w>")};
get(1051) -> #talk{talkid = 1051,content = ?T("<w><font size='16' color='#fff9d1'>     上仙要寻妖神殿总坛老夫不知，但那</font></w><w><font size='16' color='#12ff00'>老者</font></w><w><font size='16' color='#fff9d1'>所在，却就在此处不远。但此路凶险，还是先提升些实力再出发罢。    </font></w>")};
get(1052) -> #talk{talkid = 1052,content = ?T("<w><font size='16' color='#fff9d1'>     蛮荒此地猪生异象，也与别处不同。     </font></w>")};
get(1053) -> #talk{talkid = 1053,content = ?T("<w><font size='16' color='#fff9d1'>     攻打长留？谁会干那么无聊的事。我对人界没有兴趣。不相信？去问问我</font></w><w><font size='16' color='#12ff00'>旁边那个大高个儿</font></w><w><font size='16' color='#fff9d1'>去，他最不屑说谎。    </font></w>")};
get(1054) -> #talk{talkid = 1054,content = ?T("<w><font size='16' color='#fff9d1'>     圣君不屑为此，应是叛军所为。部分妖魔似中邪术，一心要放出妖神，我已派出</font></w><w><font size='16' color='#12ff00'>探子</font></w><w><font size='16' color='#fff9d1'>调查，请你前去接应。    </font></w>")};
get(1055) -> #talk{talkid = 1055,content = ?T("<w><font size='16' color='#fff9d1'>     饕餮无所不吃，实是粮食危机一大祸害。     </font></w>")};
get(1056) -> #talk{talkid = 1056,content = ?T("<w><font size='16' color='#fff9d1'>     非我族类，其心必异，此时不除更待何时。     </font></w>")};
get(1057) -> #talk{talkid = 1057,content = ?T("<w><font size='16' color='#fff9d1'>     上仙你来的正好！我已探明叛徒主将所在，奈何冲不破此处重重围困，赠你两张</font></w><w><font size='16' color='#12ff00'>飞行符</font></w><w><font size='16' color='#fff9d1'>，请你代我走一趟吧！    </font></w>")};
get(1058) -> #talk{talkid = 1058,content = ?T("<w><font size='16' color='#fff9d1'>     上，上仙，后面的路我们还是走过去吧，双飞我头晕！哎呀，那个</font></w><w><font size='16' color='#12ff00'>死要钱的</font></w><w><font size='16' color='#fff9d1'>又来了，他又有什么企图？    </font></w>")};
get(1059) -> #talk{talkid = 1059,content = ?T("<w><font size='16' color='#fff9d1'>     我观上仙战意高昂，只是要挑战魔军主将修为稍有欠缺。若上仙肯为我除去</font></w><w><font size='16' color='#12ff00'>幽冥判官</font></w><w><font size='16' color='#fff9d1'>，我这就送上仙前去修炼！    </font></w>")};
get(1060) -> #talk{talkid = 1060,content = ?T("<w><font size='16' color='#fff9d1'>     上仙真是爽快！这个消息免费赠送：前方瘴气横生，唯路旁</font></w><w><font size='16' color='#12ff00'>魔触手</font></w><w><font size='16' color='#fff9d1'>可解此毒，还请上仙早做准备~    </font></w>")};
get(1061) -> #talk{talkid = 1061,content = ?T("<w><font size='16' color='#fff9d1'>     单挑魔军总帅？倒是很有胆色，奴家喜欢你哩~你去到妖神殿下，先去寻</font></w><w><font size='16' color='#12ff00'>夏紫薰</font></w><w><font size='16' color='#fff9d1'>说话，说不定能得到援手。嘻，，可别死哦~    </font></w>")};
get(1062) -> #talk{talkid = 1062,content = ?T("<w><font size='16' color='#fff9d1'>     是你？！我虽堕入魔道，却无法对付长留的恩人，魔军总帅</font></w><w><font size='16' color='#12ff00'>巨魔尸</font></w><w><font size='16' color='#fff9d1'>十分强大，你若只有这点实力，只怕凶多吉少。    </font></w>")};
get(1063) -> #talk{talkid = 1063,content = ?T("<w><font size='16' color='#fff9d1'>     妖神殿叛军魔神之首，斩除不费吹灰之力。     </font></w>")};
get(1064) -> #talk{talkid = 1064,content = ?T("<w><font size='16' color='#fff9d1'>     你受瘴气所侵，休养一阵便好。只是魔尸虽死，操纵它的</font></w><w><font size='16' color='#12ff00'>心魔</font></w><w><font size='16' color='#fff9d1'>却无处可寻，要战胜它，得广交同伴，共同对抗才行。    </font></w>")};
get(1065) -> #talk{talkid = 1065,content = ?T("<w><font size='16' color='#fff9d1'>     昔日妖神力量被封于十件神器，残魂逸散化为心魔，潜伏于人心的阴暗面，防不胜防。你将此事告知</font></w><w><font size='16' color='#12ff00'>禁军统领</font></w><w><font size='16' color='#fff9d1'>，商讨对策。    </font></w>")};
get(1066) -> #talk{talkid = 1066,content = ?T("<w><font size='16' color='#fff9d1'>     上仙真是不世出的大英雄，大豪杰！然而独木究竟难支，红尘之中奇人不少，何不去</font></w><w><font size='16' color='#12ff00'>坊市</font></w><w><font size='16' color='#fff9d1'>走一趟，交结英豪？    </font></w>")};
get(1067) -> #talk{talkid = 1067,content = ?T("<w><font size='16' color='#fff9d1'>     啊呀，这不是我失散多年的私房钱嘛，你这个朋友我交了！听那边的</font></w><w><font size='16' color='#12ff00'>赛华佗</font></w><w><font size='16' color='#fff9d1'>说，你和他还有些交情？那老头脾气古怪，你真是不简单哪！🐖    </font></w>")};
get(1068) -> #talk{talkid = 1068,content = ?T("<w><font size='16' color='#fff9d1'>     士别三日当刮目相待，上仙大破魔军，名扬天下，那边的</font></w><w><font size='16' color='#12ff00'>红拂女</font></w><w><font size='16' color='#fff9d1'>早有与你结交之意，呵呵，需不需要老朽引荐啊？    </font></w>")};
get(1069) -> #talk{talkid = 1069,content = ?T("<w><font size='16' color='#fff9d1'>     上仙扫平魔军，奴家身无长物，只好以宝剑剑谱相赠英雄。咦，来了封飞符传书，请上仙尽快前往</font></w><w><font size='16' color='#12ff00'>东海</font></w><w><font size='16' color='#fff9d1'>……难道东海出了什么变故吗？    </font></w>")};
get(1070) -> #talk{talkid = 1070,content = ?T("<w><font size='16' color='#fff9d1'>     宝儿师妹前来替掌门求药，却遇上水族妖魔莫名作乱，难道东海的</font></w><w><font size='16' color='#12ff00'>妖神封印</font></w><w><font size='16' color='#fff9d1'>出了什么事情？上仙，请你赶紧前去看看吧！    </font></w>")};
get(1071) -> #talk{talkid = 1071,content = ?T("<w><font size='16' color='#fff9d1'>     长留的人来东海干什么！你们掌门拒收我家小姐为徒，已是奇耻大辱，还想求我蓬莱灵药？有本事</font></w><w><font size='16' color='#12ff00'>杀光这些妖魔</font></w><w><font size='16' color='#fff9d1'>再说！    </font></w>")};
get(1072) -> #talk{talkid = 1072,content = ?T("<w><font size='16' color='#fff9d1'>     饿鬼无知，唯堕无间地狱。     </font></w>")};
get(1073) -> #talk{talkid = 1073,content = ?T("<w><font size='16' color='#fff9d1'>     小骨盗取神器为掌门续命，蓬莱却硬说她妖神附体，</font></w><w><font size='16' color='#12ff00'>镇压</font></w><w><font size='16' color='#fff9d1'>于此，还羞辱我们是妖神走狗，欺人太甚！    </font></w>")};
get(1074) -> #talk{talkid = 1074,content = ?T("<w><font size='16' color='#fff9d1'>     海底波澜起，森然鬼爪寒。     </font></w>")};
get(1075) -> #talk{talkid = 1075,content = ?T("<w><font size='16' color='#fff9d1'>     冤枉？不存在什么冤枉。盗神器后她形貌俱变，分明沾染了妖神的洪荒之力，再说封印外还有一堆</font></w><w><font size='16' color='#12ff00'>水族妖魔</font></w><w><font size='16' color='#fff9d1'>想救她出来呢！    </font></w>")};
get(1076) -> #talk{talkid = 1076,content = ?T("<w><font size='16' color='#fff9d1'>     我不走，长留与蓬莱的矛盾是因我而起，请帮我求求</font></w><w><font size='16' color='#12ff00'>蓬莱前任掌门</font></w><w><font size='16' color='#fff9d1'>，若能求得灵药，我甘愿永世镇锁于此！    </font></w>")};
get(1077) -> #talk{talkid = 1077,content = ?T("<w><font size='16' color='#fff9d1'>     哼，虚情假意！白长留不肯收我女儿为徒，分明是欺我蓬莱无人！想要灵药？先去镇压了趁火打劫的</font></w><w><font size='16' color='#12ff00'>蛮族精锐</font></w><w><font size='16' color='#fff9d1'>再来和我说话！    </font></w>")};
get(1078) -> #talk{talkid = 1078,content = ?T("<w><font size='16' color='#fff9d1'>     打人撒气大约是这家人的传统，帮他打打就罢。     </font></w>")};
get(1079) -> #talk{talkid = 1079,content = ?T("<w><font size='16' color='#fff9d1'>     那老伯看起来精力十足，怎么就退位让他女儿当了掌门？其中必有内情，我们不如花些</font></w><w><font size='16' color='#12ff00'>灵石</font></w><w><font size='16' color='#fff9d1'>，向他们打探一下。    </font></w>")};
get(1080) -> #talk{talkid = 1080,content = ?T("<w><font size='16' color='#fff9d1'>     这就怪了，听她们口吻，前掌门竟是不满女儿继续与长留结交才离家出走，难道求药之事可成？快去拜访</font></w><w><font size='16' color='#12ff00'>蓬莱长老</font></w><w><font size='16' color='#fff9d1'>！    </font></w>")};
get(1081) -> #talk{talkid = 1081,content = ?T("<w><font size='16' color='#fff9d1'>     原来是长留的贵客，我家长老在此恭候多时了，请上仙</font></w><w><font size='16' color='#12ff00'>上台一叙</font></w><w><font size='16' color='#fff9d1'>。    </font></w>")};
get(1082) -> #talk{talkid = 1082,content = ?T("<w><font size='16' color='#fff9d1'>     如今妖魔异动频繁，本应一致对外，但掌门的家庭矛盾我等难以插手。上仙修为高深，可否作说客寻</font></w><w><font size='16' color='#12ff00'>老掌门</font></w><w><font size='16' color='#fff9d1'>一行？    </font></w>")};
get(1083) -> #talk{talkid = 1083,content = ?T("<w><font size='16' color='#fff9d1'>     就凭你这点能耐，也想当说客？哼，先</font></w><w><font size='16' color='#12ff00'>清扫附近的妖魔</font></w><w><font size='16' color='#fff9d1'>，显示出实力，才有和我说话的资格！    </font></w>")};
get(1084) -> #talk{talkid = 1084,content = ?T("<w><font size='16' color='#fff9d1'>     宝剑锋从磨砺出，不破妖魔誓不还。     </font></w>")};
get(1085) -> #talk{talkid = 1085,content = ?T("<w><font size='16' color='#fff9d1'>     宝剑锋从磨砺出，不破妖魔誓不还。     </font></w>")};
get(1086) -> #talk{talkid = 1086,content = ?T("<w><font size='16' color='#fff9d1'>     老掌门虽爱为难人，但都只是嘴上不饶罢了，依他本心，当是前路艰险，希望上仙</font></w><w><font size='16' color='#12ff00'>提升修为</font></w><w><font size='16' color='#fff9d1'>，再向前进。    </font></w>")};
get(1087) -> #talk{talkid = 1087,content = ?T("<w><font size='16' color='#fff9d1'>    上仙真会哄人，一朵花就想哄了奴家去？当奴家是那好骗的小娃儿么？蓬莱的</font></w><w><font size='16' color='#12ff00'>明月童子</font></w><w><font size='16' color='#fff9d1'>素来口风不紧，去套个话不妨事。    </font></w>")};
get(1088) -> #talk{talkid = 1088,content = ?T("<w><font size='16' color='#fff9d1'>     长留的人？哼，当初你们掌门拒收小姐为徒，如今有求于蓬莱，又来帮我们跑腿。也好，先</font></w><w><font size='16' color='#12ff00'>干掉妖魔</font></w><w><font size='16' color='#fff9d1'>，再来找我。    </font></w>")};
get(1089) -> #talk{talkid = 1089,content = ?T("<w><font size='16' color='#fff9d1'>     你的修为倒是提高了不少，不错不错，有资格替我跑腿了。这个锦囊需交给</font></w><w><font size='16' color='#12ff00'>蓬莱长老</font></w><w><font size='16' color='#fff9d1'>，你代我去一趟吧。    </font></w>")};
get(1090) -> #talk{talkid = 1090,content = ?T("<w><font size='16' color='#fff9d1'>     宝剑锋从磨砺出，不破妖魔誓不还。     </font></w>")};
get(1091) -> #talk{talkid = 1091,content = ?T("<w><font size='16' color='#fff9d1'>     因着白上仙收小骨作徒弟一事，长留与蓬莱结怨，听说老掌门因此和女儿吵架，还离家出走了呢！劳烦上仙你去寻老掌门，为他们说和说和。     </font></w>")};
get(1092) -> #talk{talkid = 1092,content = ?T("<w><font size='16' color='#fff9d1'>     我和女儿吵架，关你们什么事！先提高了修为，做到这几桩事再来和我说话！     </font></w>")};
get(1093) -> #talk{talkid = 1093,content = ?T("<w><font size='16' color='#fff9d1'>     谢上仙为蓬莱除此一害！老掌门人虽任性些，心地却不坏，还请上仙务必包涵。     </font></w>")};
get(1094) -> #talk{talkid = 1094,content = ?T("<w><font size='16' color='#fff9d1'>     听闻蓬莱有忘情草，能疗魂魄之伤，若能求来，或许能治好长留掌门的旧伤。只是，她会答应吗？     </font></w>")};
get(1095) -> #talk{talkid = 1095,content = ?T("<w><font size='16' color='#fff9d1'>     哦，他白长留，也有求到我头上的时候？……呵，既然如此，给他也无妨，你先提升修为再来寻我。     </font></w>")};
get(1096) -> #talk{talkid = 1096,content = ?T("<w><font size='16' color='#fff9d1'>     哼，不错嘛，拿上这株忘情花，回长安去罢。告诉蜀山多管闲事的掌门，我与父亲的事我自会处理，至于白长留……呵，我们走着瞧。     </font></w>")};
get(1097) -> #talk{talkid = 1097,content = ?T("<w><font size='16' color='#fff9d1'>     霓云蔚个性激烈，她虽与白长留结仇，却未必会危及大局。玄门四派你已见其三，昆仑隐匿多年，不去……也罢。     </font></w>")};
get(1098) -> #talk{talkid = 1098,content = ?T("<w><font size='16' color='#fff9d1'>     昆仑受神界传承，位于其掌门人开辟的云上天宫，多年已不再听闻昆仑弟子于世行走的消息。你若执意前往，可提升等级后寻长留弟子询问。     </font></w>")};
get(1099) -> #talk{talkid = 1099,content = ?T("<w><font size='16' color='#fff9d1'>     长留宗卷中确实载有前往天宫之法，有这灵石在，足以为开启传送阵之用了。不过东方先生一力劝阻，不知有何缘故？     </font></w>")};
get(1100) -> #talk{talkid = 1100,content = ?T("<w><font size='16' color='#fff9d1'>     若你执意前往，我也无意拦阻。这对鸿鹄之羽赠与你，助你翱翔九天之上。     </font></w>")};
get(1101) -> #talk{talkid = 1101,content = ?T("<w><font size='16' color='#fff9d1'>     要前往天宫，不提升修为可不行。     </font></w>")};
get(1102) -> #talk{talkid = 1102,content = ?T("<w><font size='16' color='#fff9d1'>     朽骨暗夜候多时。这里已是一座空城……你为何而来？（声音渐渐消失）     </font></w>")};
get(1103) -> #talk{talkid = 1103,content = ?T("<w><font size='16' color='#fff9d1'>     宝剑锋从磨砺出，不破妖魔誓不还。     </font></w>")};
get(1104) -> #talk{talkid = 1104,content = ?T("<w><font size='16' color='#fff9d1'>     神器被窃，掌门失踪，师兄弟们都到哪里去了？这里好暗……好冷。     </font></w>")};
get(1105) -> #talk{talkid = 1105,content = ?T("<w><font size='16' color='#fff9d1'>     你身上有掌门的气息……上仙，你来昆仑所为何事？我虽已是残魂之身，正殿中也许尚有人幸存。     </font></w>")};
get(1106) -> #talk{talkid = 1106,content = ?T("<w><font size='16' color='#fff9d1'>     宝剑锋从磨砺出，不破妖魔誓不还。     </font></w>")};
get(1107) -> #talk{talkid = 1107,content = ?T("<w><font size='16' color='#fff9d1'>     昆仑封闭多年，上仙从何而来？此地危机四伏，唯有修为高深者方能存活，上仙千万小心！     </font></w>")};
get(1108) -> #talk{talkid = 1108,content = ?T("<w><font size='16' color='#fff9d1'>     我只是来看看这里有什么宝贝，却只看到了一堆残魂。唉，看起来倒是挺可怜的，你还是快去正殿帮帮他们吧！     </font></w>")};
get(1109) -> #talk{talkid = 1109,content = ?T("<w><font size='16' color='#fff9d1'>     昆仑与世无争，却有门中叛逆将天宫所在告知妖神，一夕之间，本派被毁于这诛仙剑阵之下。劳烦上仙先除妖神精锐，为我无辜的昆仑上下出一口气。     </font></w>")};
get(1110) -> #talk{talkid = 1110,content = ?T("<w><font size='16' color='#fff9d1'>     上仙！救星！大恩人！快来救我下去！这里到处都是魂魄，吓得我都不敢动了！。     </font></w>")};
get(1111) -> #talk{talkid = 1111,content = ?T("<w><font size='16' color='#fff9d1'>     诛仙剑阵为杀戮之极，一击之下能毁陆焚城。这笔血债势必要记在妖神账上。既有人生还，你少不得要去助他一二。     </font></w>")};
get(1112) -> #talk{talkid = 1112,content = ?T("<w><font size='16' color='#fff9d1'>     洛十三那个大笨蛋，我才不去救他呢！这有一处诛仙剑阵的阵眼，我们先破了它再说~     </font></w>")};
get(1113) -> #talk{talkid = 1113,content = ?T("<w><font size='16' color='#fff9d1'>     好好一座仙宫，现在被鬼怪弄得阴森森的。我们先提升修为，再去把它们扫个干净！     </font></w>")};
get(1114) -> #talk{talkid = 1114,content = ?T("<w><font size='16' color='#fff9d1'>     诛仙剑阵降下之时，多亏掌门牺牲修为开启门派大阵，不然此处已是一片白地了……奇怪，你身上为何有掌门的气息？     </font></w>")};
get(1115) -> #talk{talkid = 1115,content = ?T("<w><font size='16' color='#fff9d1'>     你问他的下落？他修为全失，被天宫结界排斥，若还活着，应当是去了人界。你问我这个作什么？     </font></w>")};
get(1116) -> #talk{talkid = 1116,content = ?T("<w><font size='16' color='#fff9d1'>     宝剑锋从磨砺出，不破妖魔誓不还。     </font></w>")};
get(1117) -> #talk{talkid = 1117,content = ?T("<w><font size='16' color='#fff9d1'>     诛仙剑阵四只余一，束缚我们魂魄的力量终于消失了！多谢上仙，多谢上仙！     </font></w>")};
get(1118) -> #talk{talkid = 1118,content = ?T("<w><font size='16' color='#fff9d1'>     剑阵破除在望，在外失散的门人也可回到天宫，昆仑传承有望了。一事不烦劳二主，上仙可否助我扫清此处妖魔？     </font></w>")};
get(1119) -> #talk{talkid = 1119,content = ?T("<w><font size='16' color='#fff9d1'>     宝剑锋从磨砺出，不破妖魔誓不还。     </font></w>")};
get(1120) -> #talk{talkid = 1120,content = ?T("<w><font size='16' color='#fff9d1'>     上仙，唐宝，我想死你们了！~快快，我们提升修为，早点回人界去，离开这个鬼地方！     </font></w>")};
get(1121) -> #talk{talkid = 1121,content = ?T("<w><font size='16' color='#fff9d1'>     宝剑锋从磨砺出，不破妖魔誓不还。     </font></w>")};
get(1122) -> #talk{talkid = 1122,content = ?T("<w><font size='16' color='#fff9d1'>     我观上仙身上带有东方掌门的气息，若你在人界得到他的消息，请务必告知老朽。唉，昆仑上下实在亏欠掌门太多了……     </font></w>")};
get(1123) -> #talk{talkid = 1123,content = ?T("<w><font size='16' color='#fff9d1'>     你竟已经知道了……罢了，东方星纬正是昆仑前掌门，昔日真相为何，你自己去问他吧。     </font></w>")};
get(1124) -> #talk{talkid = 1124,content = ?T("<w><font size='16' color='#fff9d1'>     无力保护门派，不能救出众人，甚至连心爱的人都保全不了……我实是昆仑的罪人，无面目再见他们……你就这么回复罢。     </font></w>")};
get(1125) -> #talk{talkid = 1125,content = ?T("<w><font size='16' color='#fff9d1'>     掌门果然还在怪责自己啊……他对他人宽厚，却总对自己苛责。也罢，只要他平安活着，总有一日会解开心结。     </font></w>")};
get(1126) -> #talk{talkid = 1126,content = ?T("<w><font size='16' color='#fff9d1'>     长老对星纬的好意，我心领了，但人终究要为自己所选择的承担责任。上仙，那禁军统领似有要事寻你，你先去罢。     </font></w>")};
get(1127) -> #talk{talkid = 1127,content = ?T("<w><font size='16' color='#fff9d1'>     旧都洛阳本是龙脉所在，却因前朝废帝诅咒而荒废。如今那里集结叛军，盗匪横行，望上仙助我等前去剿灭。     </font></w>")};
get(1128) -> #talk{talkid = 1128,content = ?T("<w><font size='16' color='#fff9d1'>     上仙肯亲临前线，真是我等的强援！此处叛军勾结妖魔，势力甚大，上仙可以先去秘境中历练一番！     </font></w>")};
get(1129) -> #talk{talkid = 1129,content = ?T("<w><font size='16' color='#fff9d1'>     宝剑锋从磨砺出，不破妖魔誓不还。     </font></w>")};
get(1130) -> #talk{talkid = 1130,content = ?T("<w><font size='16' color='#fff9d1'>     江湖相逢，莫问来处，我只是一个好管闲事的闲人，上仙既然来此平叛，那边还有一位叛军精锐，也劳烦一并除去。     </font></w>")};
get(1131) -> #talk{talkid = 1131,content = ?T("<w><font size='16' color='#fff9d1'>     上仙果然好身手！这龙脉禁锁之地，竟也能来去纵横自如。当然，要再提升些修为就更好了。     </font></w>")};
get(1132) -> #talk{talkid = 1132,content = ?T("<w><font size='16' color='#fff9d1'>     真是惊险万分，那群刀手实在人数众多，还好有上仙在此。     </font></w>")};
get(1133) -> #talk{talkid = 1133,content = ?T("<w><font size='16' color='#fff9d1'>     这些叛军，好生蛮横！那老板娘连楼都不敢下，只好躲到房上去了！     </font></w>")};
get(1134) -> #talk{talkid = 1134,content = ?T("<w><font size='16' color='#fff9d1'>     叛军之中不乏妖魔之辈，自称什么翻天大王，覆海大王的，上仙遇到他们可要多加小心。     </font></w>")};
